import React, { useContext, useState } from 'react';
import {TransactionContext} from './transContext';

function Child() {
    let {transactions} = useContext(TransactionContext);
    let [newdesc, setDesc] = useState("");
    let [newAmount, setAmount] = useState(0);


    const handleAddition = (event) =>{
        event.preventDefault();
        console.log
    }

    return (
        <div className='container'>
            <h1 className="text-center">Expense Tracker</h1>

            <h3>Your Balance <br /> $260</h3>

            <div className='expense-container'>
                <h3>INCOME <br /> $500</h3>
                <h3>INCOME <br /> $240</h3>
            </div>

            <h3>History</h3>
            <hr />

            <ul className="transaction-list">
                {transactions.map((transObj, ind) => {
                    return (<li key={ind}>
                        <span>{transObj.desc}</span>
                        <span>{transObj.amount}</span>
                    </li>
                    )
                })}
            </ul>

            <h3>Add new transaction</h3>
            <hr />

            <form className="trasaction-form" onSubmit={handleAddition}>
                <label>
                    Enter Description <br />
                    <input type="text" onChange={(ev)=>setDesc(ev.targe.valve)} required />
                </label>

                <br />
                <label>
                    Enter Amount <br />
                    <input type="number" onChange={(ev)=>setAmount(ev.targe.valve)} required />
                </label>
                <br />
                <input type="submit" value="Add Transaction" />
            </form>
        </div>
    );
}

export default Child;
